/*-----------------------------------------------------------------------------------------------------


  \param cgf
  \param sz
-----------------------------------------------------------------------------------------------------*/
void  Config_pins(const T_IO_pins_configuration *cfg, uint32_t sz)
{
  T_reg_PFS pfs;
  for (uint32_t i=0; i < sz; i++)
  {
    memset(&pfs, 0,  sizeof(pfs));
    pfs.PODR  = cfg[i].init;
    pfs.PDR   = cfg[i].dir;
    pfs.PCR   = cfg[i].PCR;
    pfs.NCODR = cfg[i].ODR;
    pfs.DSCR  = cfg[i].DSCR;
    pfs.ISEL  = cfg[i].ISEL;
    pfs.ASEL  = cfg[i].ASEL;
    pfs.PMR   = cfg[i].PORTM;
    pfs.PSEL  = cfg[i].PSEL;
    memcpy((void *)cfg[i].PFS,&pfs, 4);
  }
}

