/*-----------------------------------------------------------------------------------------------------


  \param pin_num
  \param dstr
  \param str_sz
-----------------------------------------------------------------------------------------------------*/
void S7V30_get_board_pin_conf_str(uint32_t pin_num, char *dstr)
{
  char      str[32];
  uint32_t  pin = pin_num;
  uint32_t  tsz = S7V30_get_board_pin_count();
  T_reg_PFS pfs;

  dstr[0] = 0;

  if (pin_num >= tsz)
  {
    return;
  }


  sprintf(str, "%08X ,", all_ports_work_cfg[pin].PFS);
  strcat(dstr, str);

  strcat(dstr, all_ports_work_cfg[pin].name);
  strcat(dstr, " ,");


  memcpy(&pfs, (void *)all_ports_work_cfg[pin].PFS, 4);



  sprintf(str, "PSEL_%02d ,", pfs.PSEL);
  strcat(dstr, str);

  if (pfs.PCR == 0)
  {
    strcat(dstr, "PULLUP_DIS ,");
  }
  else
  {
    strcat(dstr, "PULLUP__EN ,");
  }

  if (pfs.NCODR == 0)
  {
    strcat(dstr, "OD_DIS ,");
  }
  else
  {
    strcat(dstr, "OD__EN ,");
  }


  switch (pfs.DSCR)
  {
  case 0:
    strcat(dstr, "LO_DRV ,");
    break;
  case 1:
    strcat(dstr, "MI_DRV ,");
    break;
  case 2:
    strcat(dstr, "NO_DRV ,");
    break;
  case 3:
    strcat(dstr, "HI_DRV ,");
    break;
  }

  switch (pfs.EOFR)
  {
  case 0:
    strcat(dstr, "EV_DNC ,");
    break;
  case 1:
    strcat(dstr, "EV_DRE ,");
    break;
  case 2:
    strcat(dstr, "EV_DFE ,");
    break;
  case 3:
    strcat(dstr, "EV_DBE ,");
    break;
  }


  if (pfs.ISEL == 0)
  {
    strcat(dstr, "IRQ_DIS ,");
  }
  else
  {
    strcat(dstr, "IRQ__EN ,");
  }

  if (pfs.ASEL == 0)
  {
    strcat(dstr, "ANAL_DIS ,");
  }
  else
  {
    strcat(dstr, "ANAL__EN ,");
  }

  if (pfs.PMR == 0)
  {
    strcat(dstr, "PORT__IO ,");
  }
  else
  {
    strcat(dstr, "PORT_PER ,");
  }

  if (pfs.PDR == 0)
  {
    strcat(dstr, "GP_INP ,");
  }
  else
  {
    strcat(dstr, "GP_OUT ,");
  }

  if (pfs.PODR == 0)
  {
    strcat(dstr, "0 , ");
  }
  else
  {
    strcat(dstr, "1 , ");
  }

  strcat(dstr, all_ports_work_cfg[pin].descr);

}


